import { WindowLabel } from '@tauri-apps/api/window';

/**
 * Attaches window to the desktop.
 * @param windowLabel The window label to attach the desktop to. If not provided, the current window will be used.
 */
declare const attach: (windowLabel?: WindowLabel) => Promise<unknown>;
/**
 * Detaches window from the desktop.
 * @param windowLabel The window label to detach the desktop from. If not provided, the current window will be used.
 */
declare const detach: (windowLabel?: WindowLabel) => Promise<unknown>;
/**
 * Resets the wallpaper to the default.
 */
declare const reset: () => Promise<unknown>;
declare const wallpaper: {
    /**
     * Attaches window to the desktop.
     * @param windowLabel The window label to attach the desktop to. If not provided, the current window will be used.
     **/
    attach: (windowLabel?: WindowLabel) => Promise<unknown>;
    /**
     * Detaches window from the desktop.
     * @param windowLabel The window label to detach the desktop from. If not provided, the current window will be used.
     **/
    detach: (windowLabel?: WindowLabel) => Promise<unknown>;
    /**
     * Resets the wallpaper to the default.
     **/
    reset: () => Promise<unknown>;
};

export { attach, wallpaper as default, detach, reset };
