// lib/main.ts
import { invoke } from "@tauri-apps/api/core";
import { getCurrentWindow } from "@tauri-apps/api/window";
var currentWindow = getCurrentWindow();
var action = (action2) => `plugin:wallpaper|${action2}`;
var attach = (windowLabel) => {
  if (!windowLabel) {
    windowLabel = currentWindow.label;
  }
  return invoke(action("attach" /* Attach */), {
    payload: {
      windowLabel
    }
  });
};
var detach = (windowLabel) => {
  if (!windowLabel) {
    windowLabel = currentWindow.label;
  }
  return invoke(action("detach" /* Detach */), {
    payload: {
      windowLabel
    }
  });
};
var reset = () => {
  return invoke(action("reset" /* Reset */));
};
var wallpaper = {
  /**
   * Attaches window to the desktop.
   * @param windowLabel The window label to attach the desktop to. If not provided, the current window will be used.
   **/
  attach,
  /**
   * Detaches window from the desktop.
   * @param windowLabel The window label to detach the desktop from. If not provided, the current window will be used.
   **/
  detach,
  /**
   * Resets the wallpaper to the default.
   **/
  reset
};
var main_default = wallpaper;
export {
  attach,
  main_default as default,
  detach,
  reset
};
